package main

import "testing"

func TestSum(t *testing.T) {
    tests := []struct{a,b,exp int}{ {1,2,3}, {5,7,12}, {-1,1,0} }
    for _, tt := range tests {
        got := Sum(tt.a, tt.b)
        if got != tt.exp {
            t.Fatalf("Sum(%d,%d) = %d; want %d", tt.a, tt.b, got, tt.exp)
        }
    }
}
